func testContractFact(bytecode string, data []byte, t *testing.T) {
	// init data
	contractId, txContext, byteCode := test.InitContextTest(pb.RuntimeType_EVM)
	byteCode, err := hex.DecodeString(bytecode)
	if err != nil {
		panic(err)
	}
	if len(byteCode) == 0 {
		panic("error byteCode==0")
	}

	//param
	//method := "calcSha256"
	//myAbi, err := abi.JSON(strings.NewReader(abiJSON))
	//if err != nil {
	//	panic(err)
	//}
	//if _, ok := myAbi.Methods[method]; !ok {
	//	panic("expected 'balance' to be present")
	//}
	//data, err := myAbi.Pack(method,"123")
	//data, err := myAbi.Pack(method, []*big.Int{big.NewInt(23333),big.NewInt(233)})
	if err != nil {
		panic(err)
	}

	// send
	start := time.Now().UnixNano() / 1e6
	x := 0
	wg := sync.WaitGroup{}
	for i := 0; i < 1; i++ {
		for j := 0; j < 1; j++ {
			x += 1
			y := int32(x)
			wg.Add(1)
			go func() {
				defer wg.Done()
				invokeCallContractTestSave(hex.EncodeToString(data), int32(i), contractId, txContext, byteCode)
				end := time.Now().UnixNano() / 1e6
				if (end-start)/1000 > 0 && y%100 == 0 {
					fmt.Printf("【tps】 %d 【spend】%d i = %d, count=%d \n", int(y)/int((end-start)/1000), end-start, i+1, y)
				}
			}()
		}
		wg.Wait()
	}

	end := time.Now().UnixNano() / 1e6
	println("end 【spend】", end-start)
	//time.Sleep(time.Second * 5) // 73m
}

func invokeCallContractTestSave(method string, id int32, contractId *pb.ContractId, txContext protocol.TxSimContext, byteCode []byte) {
	parameters := make(map[string]string)
	test.BaseParam(parameters)
	parameters["data"] = method

	runtimeInstance := &evm.RuntimeInstance{
		Log:          logger.GetLogger(logger.MODULE_VM),
		TxSimContext: txContext,
		ChainId:      test.ChainIdTest,
	}
	result := runtimeInstance.Invoke(contractId, method[0:8], byteCode, parameters, txContext, 0)
	fmt.Println("==============\n", result, "=====================")
}
func TestMy(t *testing.T) {
	res := 1.0
	for i := 0.0; i < 50; i++ {
		res *= (366.0 - i) / 366.0
		t.Log(res, "      now i=", i)
	}
	t.Log(res)
}
func TestY2(t *testing.T) {
	set := make(map[int]bool)
	ret := 0
	rand.Seed(time.Now().UnixNano())
	for j := 0; j < 100; j++ {
		for i := 1; i < 51; i++ {
			r := rand.Intn(365)
			set[r] = !set[r]
			if !set[r] {
				set = make(map[int]bool)
				ret += 1
				t.Log(i, ret)
				break
			}
		}
	}
}

